    <!-- dropzone.js -->
    <script src="https://unpkg.com/dropzone@5/dist/min/dropzone.min.js"></script>

    <script>
        Dropzone.autoDiscover = false;
        const myDropzone = new Dropzone("#predict-dropzone", {
            url: "/api/v1/public/predict",
            maxFiles: 1,
        });
        myDropzone.on('success', function(file, response) {
            const resText = response.data.result == 0 ? "Normal" : "MI";
            $("#result-text").text(resText);

            $('#predict-dropzone').on("hidden.bs.collapse", () => {
                $('#predict-result').collapse("show");
            });
            $('#predict-dropzone').collapse("hide");
        });

        $("button#clear").click(function() {
            myDropzone.removeAllFiles(true);
            $('#predict-result').on("hidden.bs.collapse", () => {
                $('#predict-dropzone').collapse("show");
            });
            $('#predict-result').collapse("hide");
        });

        $(document).ready(function() {
            $('#predict-result').collapse("hide");
            $('#predict-dropzone').collapse("show");
        });
    </script>
